package com.example.googlemap

import android.graphics.Point
import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import com.google.android.gms.maps.model.LatLng
import com.google.android.gms.maps.model.MarkerOptions
import java.util.*

class MainActivity : AppCompatActivity(), OnCameraChangeListener, OnMapInitializedListener,
    OnMapClickListener, OnCameraMoveListener, OnMapMarkerClickListener,
    OnInfoWindowClickListener, OnLatLngScreenLocationCallback {

    private var map: MyMapView? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        map = findViewById(R.id.map)
        map?.onMapClickListener = this
        map?.onCameraChangeListener = this
        map?.onCameraMoveListener = this
        map?.onMapMarkerClickListener = this
        map?.onMapInitializedListener = this
        map?.onInfoWindowClickListener = this
        map?.initialize(supportFragmentManager)
    }

    private fun addMarker(title: String, latLng: LatLng, id: Int) {
        addMarker(title, latLng, id, null, 0, 0)
    }

    private fun addMarker(
        title: String, latLng: LatLng, id: Int,
        divIconHtml: String?, iconWidth: Int, iconHeight: Int
    ) {
        map?.addMarker(
            AirMapMarker.Builder<MarkerOptions>()
                .id(id.toLong())
                .position(latLng)
                .title(title)
                .iconId(R.mipmap.icon_location_pin)
                .divIconHtml(divIconHtml)
                .divIconWidth(iconWidth)
                .divIconHeight(iconHeight)
                .build()
        )
    }

    override fun onCameraChanged(latLng: LatLng, zoom: Int) {
        TODO("not implemented") //To change body of created functions use File | Settings | File Templates.
    }

    override fun onMapInitialized() {
        val wfcLatLng = LatLng(39.918786, 116.459273)

        addMarker("WFC", wfcLatLng, 1)
        addMarker("Chaoyang Gate", LatLng(39.923823, 116.433666), 2)
        val image =
            "<img width=\"25\" height=\"41\" src=\"data:image/png;base64,\"/>"
        addMarker("Dongzhi Gate", LatLng(39.941823, 116.426319), 3, image, 25, 41)
        map?.animateCenterZoom(wfcLatLng, 11)

        val airbnbLatLng = LatLng(37.771883, -122.405224)
        addMarker("Airbnb HQ", airbnbLatLng, 1)
        addMarker("Performance Bikes", LatLng(37.773975, -122.40205), 2)
        addMarker("REI", LatLng(37.772127, -122.404411), 3)
        addMarker("Mapbox", LatLng(37.77572, -122.41354), 4)
        map?.animateCenterZoom(airbnbLatLng, 10)

    }

